import React from 'react';
import TodoListContext from '../Contexts/TodoListContext';

class Provider extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      disabledBtn: true,
      textTodo: '',
      itemSelected: '',
      list: [],
    };
    this.disableBtn = this.disableBtn.bind(this);
    this.removeItem = this.removeItem.bind(this);
    this.changeTextTodo = this.changeTextTodo.bind(this);
    this.selectedItem = this.selectedItem.bind(this);
  }

  disableBtn() {
    const { listTodo } = this.context;
    if (listTodo.length > 0) {
      return this.setState({ disabledBtn: false });
    };
    return this.setState({ disabledBtn:true });
  };

  changeTextTodo(value) {
    this.setState({ textTodo: value });
  };

  selectedItem(value) {
    this.setState({ selectedItem: value });
  };

  async addItem(value) {
    const { list } = this.state;
    const newArr = [...list, value];
    this.setState({
      list: newArr,
      textTodo: '',
    });
    this.disableButton();

  };

  async removeItem() {
    const { selectedItem, listTodo } = this.context;
    const index = listTodo.findIndex((e) => e === selectedItem);
    const arrCopied = [...listTodo];
    arrCopied.splice(index, 1);
    this.setState({ list: arrCopied });
    this.disableBtn();
  };

  render() {
    const contextObj = {
      ...this.state,
    }
    return (
      <TodoListContext.Provider value={contextObj}>
        {this.props.children}
      </TodoListContext.Provider>
    )
  }
}

Provider.contextType = TodoListContext;

export default Provider;
