import React, { createContext, useState } from 'react';

const TodoListContext = createContext();

const TodoProvider = ({ children }) => {
  // constructor(props) {
  //   super(props);
  //   this.state = {
  //     disabledBtn: true,
  //     textTodo: '',
  //     selectedItem: '',
  //     list: [],
  //   };
  //   this.removeItem = this.removeItem.bind(this);
  //   this.changeTextTodo = this.changeTextTodo.bind(this);
  //   this.selectedItem = this.selectedItem.bind(this);
  //   this.addItem = this.addItem.bind(this);
  // }

  const [disabledBtn, setDisabledBtn] = useState(true);
  const [textTodo, setTextTodo] = useState('');
  const [selectedItem, setSelectedItem] = useState('');
  const [list, setList] = useState([]);

  const disableBtn = () => {
    if (list.length > 0) {
      return setDisabledBtn(false);
    };
    return setDisabledBtn(true);
  };

  const changeTextTodo = (value) => {
    console.log(value)
    setTextTodo(value);
  };

  const selectedItemFunc = (value) => {
    setSelectedItem(value);
  };

  const addItem = (textTodo) => {
    const newArr = [...list, textTodo];
    console.log(newArr)
    setList(newArr)
    setTextTodo('');
    setDisabledBtn(false);    
  };

  const removeItem = () => {
    const index = list.findIndex((e) => e === selectedItem);
    const arrCopied = [...list];
    arrCopied.splice(index, 1);
    setList(arrCopied);
    disableBtn()

  };


  const contextObj = {
    disabledBtn,
    addItem,
    removeItem,
    selectedItemFunc,
    changeTextTodo,
    textTodo,
    list,
    selectedItem,
  }

  return (
    <TodoListContext.Provider value={contextObj}>
      {children}
    </TodoListContext.Provider>
  )
}

export { TodoProvider, TodoListContext };
