import React, { useContext } from 'react';
import { DetailsPageContext } from './DetailsPageProvider';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import './RecipeButtonControl.css';

var today = new Date();
var dd = String(today.getDate()).padStart(2, '0');
var mm = String(today.getMonth() + 1).padStart(2, '0');
var yyyy = today.getFullYear();
today = dd + '/' + mm + '/' + yyyy;

const RecipeButtonControl = (props) => {
  const { type, id } = props;
  const { providerData:
    { idMeal, strArea, strCategory, strAlcoholic = '', strMeal, strMealThumb, strTags }
  } = useContext(DetailsPageContext);

  const startingRecipe = () => {
    const doneRecipes = [{
      id: idMeal,
      type,
      area: strArea,
      category: strCategory,
      alcoholicOrNot: strAlcoholic,
      name: strMeal,
      image: strMealThumb,
      doneData: today,
      tags: []
    }]
    localStorage.setItem('doneRecipes', JSON.stringify(doneRecipes))
  }

  if (JSON.parse(localStorage.getItem('doneRecipes')).some((recipe) => recipe.id === id)) {
    return (
      <div>
        <Link to={`/${type}/${id}/in-progress`}>
          <button type="button">
            Continuar Receita
          </button>
        </Link>
      </div>
    );
  }
  return (
    <div>
      <Link className="recipe-btn" data-testid="start-recipe-btn" to={`/${type}/${id}/in-progress`}>
        <button type="button" onClick={startingRecipe}>
          Iniciar Receita
        </button>
      </Link>
    </div>
  );
};

export default RecipeButtonControl;

RecipeButtonControl.propTypes = {
  type: PropTypes.string,
  id: PropTypes.string,
  setInProgress: PropTypes.func,
  inProgress: PropTypes.bool,
};

RecipeButtonControl.defaultProps = {
  type: '',
  id: undefined,
  setInProgress: () => {},
  inProgress: false,
};
