import React, { useEffect, useContext } from 'react';
import MealsComponent from './MealsComponent';
import DrinksComponent from './DrinksComponent';
import { DetailsPageContext } from './DetailsPageProvider';
import { fetchMealById } from '../../services/theMealAPI';
import { fetchDrinkById } from '../../services/theCockTailAPI';


const DetailsPage = (props) => {
  const {
    isLoading,
    errorMessage,
    apiRequestFunction,
  } = useContext(DetailsPageContext);

  const { location: { pathname }, match: { params: { id } }} = props;

  useEffect(() => {
    pathname.includes('/comidas') ? apiRequestFunction(fetchMealById, id)
      :
    apiRequestFunction(fetchDrinkById, id);
  }, [])

  if (isLoading) return <div>Loading...</div>
  if (errorMessage !== '') return <span>Algum Error Ocorreu</span>
  if (pathname.includes('/comidas')) return <div><MealsComponent /></div>
  return <div><DrinksComponent /></div>
}

export default DetailsPage;
