import React, { useContext } from 'react';
import { ingredientsWithQuantity } from '../../../untils/ingredientsWithQuantity';
import { RecipeInProgressContext } from '../RecipeInProgressProvider';

const IngredientsCheckbox = () => {
  const { recipeData } = useContext(RecipeInProgressContext);
  const { init, mid, end, data } = recipeData;
  console.log(recipeData);
  const ingredientsValues = Object.values(data).slice(init, mid);
  const ingredientsQuantity = Object.values(data).slice(mid, end);

  return (
    <div>
      <h2>Ingredients</h2>
      {ingredientsWithQuantity(ingredientsValues, ingredientsQuantity)
        .map(([ingredients, quantity]) => {
          (<input type="checkbox" />)
        })
      }
    </div>
  );
};

export default IngredientsCheckbox;
