import React, { useState, useEffect } from 'react';
import { fetchDrinks } from '../../../services/theCockTailAPI';

const RecommendedDrinks = () => {
  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const apiRequestSucceedDrink = ({ drinks }) => {
    setData(drinks[0]);
    setIsLoading(false);
  }

  const apiRequestFailure = ({ message }) => {
    setErrorMessage(message);
    setIsLoading(false);
  }

  useEffect(() => {
    const apiRequestFunction = (quantity) => {
      fetchDrinks(quantity).then(apiRequestSucceedDrink, apiRequestFailure)
    }
    apiRequestFunction(12)
  }, [])


