import React, { useState, createContext } from 'react';
import PropTypes from 'prop-types';
// import { fetchMealById } from '../../services/theMealAPI';

const destructureMeal = (data) => {
  const {
    strMeal:name,strCategory:category, strMealThumb:img, strYoutube:video,
    strInstructions:instructions, idMeal:id,
  } = data;
  const dataObj = {
    ...data, name, category, img, video, instructions, id, init: 9, mid: 29, end: 49, type: 'comidas',
  };
  return dataObj;
};

const destructureDrinks = (data) => {
  const {
    strDrink:name, strAlcoholic:category, strDrinkThumb:img,
    strInstructions:instructions, idDrink:id,
  } = data;
  const dataObj = {
    name, category, img, instructions, id, init: 21, mid: 36, end: 51, type: 'bebidas',
  }
  return dataObj;
};

export const DetailsPageContext = createContext();

export const DetailsPageProvider = ({ children }) => {
  const [providerData, setProviderData] = useState([]);
  const [providerRecommended, setProviderRecommended] = useState([]);
  const [pathName, setPathName] = useState('');

  const setProviderRecommendedFunc = (recommendedData) => setProviderRecommended(recommendedData);

  const setProviderDataFunc = (data) => {
    if (data.idMeal) return setProviderData(destructureMeal(data));
    setProviderData(destructureDrinks(data));
  }

  const setPathNameFunc = (path) => setPathName(path);

  const detailsPageObj = {
    setPathNameFunc,
    pathName,
    setProviderDataFunc,
    providerData,
    setProviderRecommendedFunc,
    providerRecommended,
  };

  return (
    <DetailsPageContext.Provider value={detailsPageObj}>
      {children}
    </DetailsPageContext.Provider>
  );
};

DetailsPageProvider.propTypes = {
  children: PropTypes.oneOfType([
    PropTypes.arrayOf(PropTypes.node),
    PropTypes.node,
  ]).isRequired,
};
