import React, { useContext } from 'react';
import DetailsPageContext from './DetailsPageProvider';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import './RecipeButtonControl.css';

const RecipeButtonControl = (props) => {
  const { type, id } = props;
  const { setStartedRecipesFunc, startedRecipes } = useContext(DetailsPageContext);
  if (startedRecipes.some((idStarted) => idStarted === id)) {
    return (
      <div>
        <Link to={`/${type}/${id}/in-progress`}>
          <button type="button">
            Continuar Receita
          </button>
        </Link>
      </div>
    );
  }
  return (
    <div>
      <Link className="recipe-btn" data-testid="start-recipe-btn" to={`/${type}/${id}/in-progress`}>
        <button type="button" onClick={() => setStartedRecipesFunc(id)}>
          Iniciar Receita
        </button>
      </Link>
    </div>
  );
};

export default RecipeButtonControl;

RecipeButtonControl.propTypes = {
  type: PropTypes.string,
  id: PropTypes.string,
  setInProgress: PropTypes.func,
  inProgress: PropTypes.bool,
};

RecipeButtonControl.defaultProps = {
  type: '',
  id: undefined,
  setInProgress: () => {},
  inProgress: false,
};
