import React, { useState, createContext } from 'react';
import { fetchMealById } from '../../services/theMealAPI';
import PropTypes from 'prop-types';

export const DetailsPageContext = createContext();

export const DetaislPageProvider = ({ children }) => {

  const [data, setData] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const apiRequestSucceed = ({ meals }) => {
    setData(meals[0]);
    setIsLoading(false);
  }

  const apiRequestFailure = ({ message }) => {
    setErrorMessage(message);
    setIsLoading(false);
  }

  const apiRequestFunction = (callback, id) => {
    setIsLoading(true);
    callback(id)
      .then(apiRequestSucceed, apiRequestFailure);
  }

  const detailsPageObj = {
    data,
    isLoading,
    errorMessage,
    apiRequestFunction,
  }

  return (
    <DetailsPageContext.Provider value={detailsPageObj}>
      {children}
    </DetailsPageContext.Provider>
  )
}

DetaislPageProvider.propTypes = {
  children: PropTypes.oneOfType([
    PropTypes.arrayOf(PropTypes.node),
    PropTypes.node,
  ]).isRequired,
}
