import React, { useContext } from 'react';
import PropTypes from 'prop-types';
import { RecipeInProgressContext } from '../../RecipesInProgress/RecipeInProgressProvider';
import ingredientsWithQuantity from '../../../untils/ingredientsWithQuantity';

const Ingredients = () => {
  const { recipeData: { ingredients } } = useContext(RecipeInProgressContext);
  // const { init, mid, end } = props.data;
  // const ingredientsValues = Object.values(recipeData).slice(init, mid);
  // const ingredientsQuantity = Object.values(recipeData).slice(mid, end);
  console.log(recipeData)
  return (
    <div>
      <h2>Ingredients</h2>
      <ul>
        {ingredients.map(([ingredient, quantity], index) =>
          (<li data-testid={`${index}-ingredient-name-and-measure`} key={ingredient}>{ingredient} - {quantity}</li>))}
      </ul>
    </div>
  );
};

export default Ingredients;

Ingredients.propTypes = {
  data: PropTypes.objectOf(PropTypes.any).isRequired,
};
