import React from 'react';
// import SearchBarInput from './SearchBarInputs';
import inputs from './data';

class SearchBar extends React.Component {
  constructor(props) {
    super(props);
    const { searchText, onSearchTextChange } = this.props;
    function text() {
      return (
        <label htmlFor="searchText">
          Inclui o texto
          <input
            type="text"
            id="searchText"
            name="searchText"
            onChange={onSearchTextChange}
            value={searchText}
          />
        </label>
      );
    }
  }

  render() {
    const {
      searchText, onSearchTextChange, bookmarkedOnly,
      onBookmarkedChange, selectedGenre, onSelectedGenreChange,
    } = this.props;
    return (
      <form>
        {/* <SearchBarInput type="text" id="searchText" name="searchText" onChange={onSearchTextChange} value={searchText} htmlFor="searchText" /> */}
        {/* <label htmlFor="searchText">
          Inclui o texto
          <input
            type="text"
            id="searchText"
            name="searchText"
            onChange={onSearchTextChange}
            value={searchText}
          />
        </label> */}
        {/* {inputs.map((e) => (
          <label key={e.id} htmlFor={e.htmlFor}>
            {e.label}
            <input
              type={e.type}
              id={e.id}
              name={e.name}
              onChange={e.onChange}
              value={e.value}
            />
          </label>
        ))} */}
        <label htmlFor="favoritos">
          Mostrar somente favoritos
          <input type="checkbox" id="favoritos" name="bookmarkedOnly" onChange={onBookmarkedChange} checked={bookmarkedOnly} />
        </label>
        <label htmlFor="select">
          Filtrar por gênero
          <select id="select" name="selectedGenre" onChange={onSelectedGenreChange} value={selectedGenre}>
            <option value="">Todos</option>
            <option value="action">Ação</option>
            <option value="comedy">Comédia</option>
            <option value="thriller">Suspense</option>
          </select>
        </label>
      </form>
    );
  }
}

export default SearchBar;
