import React from 'react';

class MovieCard extends React.Component {
  render() {
    const { movie } = this.props;
    const { title, subtitle, storyline, rating, imagePath } = movie;
    return (
      <div >
        <img src={imagePath} alt="Movie Cover" />
        <div >
          <h4 >{title}</h4>
          <h5 >{subtitle}</h5>
          <p >{storyline}</p>
        </div>
      </div>
    );
  }
}

export default MovieCard;
