import React, { Component } from 'react';
import MovieCard from '../components/MovieCard';
import Loading from '../components/Loading';

import * as movieAPI from '../services/movieAPI';

class MovieList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      movies: [],
    }
  }

  componentDidMount() {
    const { movies } = this.state;
    if(this.props.location.state) {
      movieAPI.getMovies().then((resp) => {
        this.setState({ movies: resp, })
      })
      const index = movies.findIndex((e) => e.id === this.props.location.state.id)
      const newArrMovies = [...movies]
      newArrMovies[index] = this.props.location.state;
      this.setState({ movies: newArrMovies })
    }

  }

  componentDidUpdate() {

  }

  render() {
    const { movies } = this.state;
    if (movies.length === 0) {
      return (
        <div>
          <Loading />
        </div>
      );
    } else {
      return (
        <div data-testid="movie-list">
          {movies.map((movie) => <MovieCard key={movie.title} movie={movie} />)}
        </div>
      );
    }

  }
}

export default MovieList;
