import React from 'react';

class PaymentMethods extends React.Component {
  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    const { name, value } = event.target;
    const { onChange } = this.props;
    onChange(name, value);
  }

  creditCardInputCreator(title, ) {
    return (
      <label htmlFor="credit-card">
        Visa
        <input type="radio" name="payment-method" id="credit-card" />
      </label>
    )
  }

  render() {
    return (
      <div>
        <h3>Método de Pagamento</h3>
        <div>
          <h4>Boleto</h4>
          <input type="radio" name="payment-method" id="billet" />
          <img src="" alt="Código de barra" />
        </div>
        <div>
          <h4>Cartão de Crédito</h4>
          <label htmlFor="visa">
            Visa
            <input type="radio" name="payment-method" id="visa" />
          </label>
          <label htmlFor="mastercard">
            MasterCard
            <input type="radio" name="payment-method" id="mastercard" />
          </label>
          <label htmlFor="elo">
            Elo
            <input type="radio" name="payment-method" id="elo" />
          </label>
        </div>
      </div>
    );
  }
}

export default PaymentMethods;
