import React from 'react';
import product from './dataTest';
import './ProductDetails.css';
import * as api from '../services/api';

class ProductDetails extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      product: [],
    }
  }

  componentDidMount = () => {
    const { category, id } = this.props.match.params;
    api.getProductsFromCategoryAndQuery(category).then((resp) => {
      // const product = resp.find((e) => e.id === id)
      // this.setState({ product })
      console.log(resp);
    })
  } 

  render() {
    console.log(this.props.match.params);
    const { title, price, thumbnail } = product.results[0];
    const productSpec = product.results[0].attributes;
    return (
      <div className="product-details-page-container">
        <div className="product-details-h1-name">
          <h1 data-testid="product-detail-name">{title}</h1>
          <h2>{`R$ ${price}`}</h2>
        </div>
        <div className="produc-details-contents">
          <div className="product-details-left">
            <img src={thumbnail} alt={`Foto do ${title}`} />
          </div>
          <div className="product-details-right">
            <h3>Especificações Técnicas</h3>
            <ul>
              {productSpec.map((e) => (
                <li key={e.id}>{`${e.name}: ${e.value_name}`}</li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    );
  }
}

export default ProductDetails;
