import React from 'react';

class BuyersInfo extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: '',
      email: '',
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    const { name, value } = event.target;
    this.setState({
      [name]: value,
    });
  }

  inputCreator(htmlForID, title, type, name, value) {
    return (
      <label htmlFor={htmlForID}>
        {title}
        <input type={type} id={htmlForID} name={name} onChange={this.handleChange} value={value} />
      </label>
    );
  }

  render() {
    const { value, email } = this.state;
    return (
      <form>
        {this.inputCreator('name', 'Nome completo', 'text', 'value', value)}
        <label htmlFor="name">
          Nome completo
          <input type="text" id="name" name="value" onChange={this.handleChange} value={value} />
        </label>
        <label htmlFor="email">
          E-mail
          <input type="email" id="email" name="email" onChange={this.handleChange} value={email} />
        </label>
      </form>
    );
  }
}

export default BuyersInfo;
