import React from 'react';

class BuyersInfo extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: '',
      email: '',
      cpf: null,
      phone: null,
      cep: null,
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    const { name, value } = event.target;
    this.setState({
      [name]: value,
    });
  }

  inputCreator(testId, htmlForID, title, type, name, value) {
    return (
      <label htmlFor={htmlForID}>
        <input
          data-testid={testId}
          type={type}
          id={htmlForID}
          name={name}
          placeholder={title}
          onChange={this.handleChange}
          value={value}
        />
      </label>
    );
  }

  render() {
    const { value, email, cpf, phone, cep } = this.state;
    return (
      <form>
        {this.inputCreator('checkout-fullname', 'name', 'Nome completo', 'text', 'value', value)}
        {this.inputCreator('checkout-email', 'email', 'E-mail', 'email', 'email', email)}
        {this.inputCreator('checkout-cpf', 'cpf', 'CPF', 'number', 'cpf', cpf)}
        {this.inputCreator('checkout-phone', 'phone', 'Telefone', 'number', 'phone', phone)}
        {this.inputCreator('checkout-cep', 'cep', 'CEP', 'number', 'cep', cep)}


      </form>
    );
  }
}

export default BuyersInfo;
