import React from 'react';
import './PaymentMethods.css';
import mastercard from '../../images/mastercard.png';
import visa from '../../images/visa.png';
import creditcard from '../../images/creditcard.png';
import barras from '../../images/barras.png';

class PaymentMethods extends React.Component {
  // constructor(props) {
  //   super(props);
    // this.handleChange = this.handleChange.bind(this);
  // }

  // handleChange(event) {
  //   const { name, value } = event.target;
  //   const { onChange } = this.props;
  //   onChange(name, value);
  // }

  creditCardInputCreator(title, htmlForId) {
    return (
      <label htmlFor={htmlForId}>
        <input
          type="radio"
          onChange={this.handleChange}
          name="payMethod"
          id={htmlForId}
          value={title}
        />
        {title}
      </label>
    );
  }

  render() {
    return (
      <div className="payment-method-container">
        <h3>Método de Pagamento</h3>
        <div className="billet-credit-card">
          <div>
            <h4>Boleto</h4>
            <label htmlFor="billet">
              <input
                type="radio"
                onChange={this.handleChange}
                name="payMethod"
                id="billet"
                value="Boleto"
              />
              <img src={barras} alt="Código de barra" />
            </label>
          </div>
          <div>
            <h4>Cartão de Crédito</h4>
            {this.creditCardInputCreator('Visa', 'visa')}
            <img src={visa} alt="Credit Visa" />
            {this.creditCardInputCreator('MasterCard', 'mastercard')}
            <img src={mastercard} alt="Credit Card MasterCard" />
            {this.creditCardInputCreator('Elo', 'elo')}
            <img src={creditcard} alt="Credit Card Elo" />
          </div>
        </div>
      </div>
    );
  }
}

export default PaymentMethods;
