import React from 'react';
import GameBoard from './GameBoard';
import './GameBoard.css';
import o from './o.svg';
import x from './x.png';

class TicTacToe extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      activePlayer: 1,
      signal: o,
      gameBoard: [0, 0, 0, 0, 0, 0, 0, 0, 0],
    };
    this.selectDiv = this.selectDiv.bind(this);
  }

  selectDiv(id) {
    const { activePlayer, gameBoard } = this.state;
    const arrCopied = gameBoard;
    arrCopied[id] = activePlayer;
    if (activePlayer === 1) {
      return this.setState({
        activePlayer: 2,
        gameBoard: arrCopied,
      });
    }
    return this.setState({
      activePlayer: 1,
      gameBoard: arrCopied,
    });
  }

  render() {
    return (
      <div className="container-tic-tac-toe">
        <GameBoard handleClick={this.selectDiv} gameState={this.state.gameBoard} />
      </div>
    )
  }
}

export default TicTacToe;
