import React from 'react';
import GameBoard from './GameBoard';
import './GameBoard.css';

class TicTacToe extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      activePlayer: 1,
      gameBoard: [0, 0, 0, 0, 0, 0, 0, 0, 0],
    };
    this.selectDiv = this.selectDiv.bind(this);
  }

  componentDidUpdate() {
    const { gameBoard, activePlayer } = this.state;
    if (gameBoard[0] === gameBoard[1] === gameBoard[2]
        || gameBoard[3] ===gameBoard[4] ===gameBoard[5]
        || gameBoard[6] ===gameBoard[7] ===gameBoard[8]) {
          alert(`Playe ${activePlayer} ganhou!`)
        }
  }

  selectDiv(id) {
    const { activePlayer, gameBoard } = this.state;
    const arrCopied = gameBoard;
    arrCopied[id] = activePlayer;
    if (activePlayer === 1) {
      return this.setState({
        activePlayer: 2,
        gameBoard: arrCopied,
      });

    }
    return this.setState({
      activePlayer: 1,
      gameBoard: arrCopied,
    });
  }

  render() {
    const { gameBoard } = this.state;
    return (
      <div className="container-tic-tac-toe">
        <GameBoard handleClick={this.selectDiv} gameState={gameBoard} />
      </div>
    )
  }
}

export default TicTacToe;
