import { ADD_TODO } from '../actions/addInputAction';

const INITIAL_STATE = {
  list: [],
};

const addInputReducer = (state = INITIAL_STATE, action) => {
  switch(action.type) {
    case ADD_TODO:
      return {
        ...state,
        list: [...state.list, action.text],
      };
      case REMOVE_TODO:
        return {
          ...state,
          list: [action.list],
        };
    default:
      return state;
  }
}

export default addInputReducer;
