import React from 'react';
import { connect } from 'react-redux';
import propTypes from 'prop-types';
import { fetchGotApi } from '../actions/apiRequestActions';
import './SearchForm.css';

class SearchForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      inputText: '',
      characterSearched: '',
    };
    this.handleChange = this.handleChange.bind(this);
    this.submitName = this.submitName.bind(this);
  }

handleChange(e) {
  this.setState({
    inputText: e.target.value,
    characterSearched: '',
  })
}

submitName(e) {
  e.preventDefault();
  const { inputText, characterSearched } = this.state;
  const { searchDispatch } = this.props;
  //desestruture a ação do thunk como propriedade aqui

  this.setState({
    inputText: '',
    characterSearched: inputText,
  }, () => {
    searchDispatch(characterSearched)
  })
  // insira a action a ser despachada para o thunk

}

render() {
  const { inputText } = this.state;
  return (
    <div>
      <form onSubmit={this.submitName}>
        <h1>Type a character below:</h1>
        <input onChange={this.handleChange}
        placeholder="Enter Character"
        value={inputText}
        />
        <div className="buttonSection">
          <button className="submitButton" type="submit" >Search!</button>
        </div>
      </form>
    </div>
  )
}
};

// mapeie as ações despachadas como propriedade do componente
const mapDispatchToProps = (dispatch) => ({
  searchDispatch: (textTyped) => dispatch(fetchGotApi(textTyped)),
})


export default connect(null, mapDispatchToProps)(SearchForm);

